import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../Css/styles.css";
import Card from "./Card";

import FileUpload from "./FileUpload";
import DatabaseSTS from "./DatabaseSTS";

const LoadSTS = () => {
  const [selectedCard, setSelectedCard] = useState(null);

  const handleCardClick = (index) => {
    setSelectedCard(index);
  };

  const cards = [
    {
      title: "SQLQueries",
      description: "Add queries",
      linkTo: "/load-sts-query",
    },
    {
      title: "File",
      description: "Upload a file",
      linkTo: "/load-sts-file",
    },
    {
      title: "Database",
      description: "Existing STS",
      linkTo: "/load-sts-database",
    },
  ];
  const SelectedComponent =
    selectedCard !== null && cards[selectedCard].componentName;

  return (
    <div className=" form">
      <h4>Load SQL Tuning Set by:</h4>
      <br />
      <br />
      <br />
      <div className="parent-container">
        <div className="card-container">
          {cards.map((card, index) => (
            <Link key={index} to={card.linkTo}>
              <Card
                title={card.title}
                description={card.description}
                handleClick={() => handleCardClick(index)}
                isSelected={selectedCard === index}
              />
            </Link>
          ))}
        </div>
        {selectedCard !== null && (
          <div className="modal">
            <div className="modal-content">
              <h2>{cards[selectedCard].title}</h2>
              <button
                className="btn-red btn-cross"
                onClick={() => setSelectedCard(null)}
              >
                X
              </button>
            </div>
          </div>
        )}
      </div>
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
    </div>
  );
};

export default LoadSTS;
